            /// <summary>
            /// IPMC bytes sent
            /// </summary>
            public long IPMCBsent;
            /// <summary>
            /// IPMC packets received
            /// </summary>
            public long IPMCrcvd;
            /// <summary>
            /// IPMC bytes received
            /// </summary>
            public long IPMCBrcvd;
